/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable no-underscore-dangle */
/* eslint-disable no-script-url */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable react/prop-types */
import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Layout, Menu, Avatar, Dropdown, Input, Button } from 'antd'
import Swal from 'sweetalert2'
import UserService from 'services/user.service'
import { jwtToken, TEACHER } from 'utils/constant'
import './MainHeader.style.scss'

const { Header } = Layout
const { SubMenu } = Menu
const { Search } = Input

const MainHeader = ({ currentUser, handleLogout, onAuthenticate }) => {
  useEffect(() => {
    const token = UserService.getPreferences(jwtToken)
    if (!currentUser && token) onAuthenticate(token)
  }, [currentUser, onAuthenticate])

  const userOptions = {
    viSignin: 'đăng nhập',
    viSignup: 'đăng ký',
    viStudent: 'Học sinh',
    viTeacher: 'Giáo viên',
    enSignin: 'login',
    enSignup: 'register',
    enStudent: 'student',
    enTeacher: 'teacher',
  }

  /* inputOptions can be an object or Promise */
  const inputOptions = new Promise(resolve => {
    setTimeout(() => {
      resolve({
        0: userOptions.viStudent,
        1: userOptions.viTeacher,
      })
    }, 1000)
  })

  const showConfirm = confirmAction => {
    Swal.fire({
      title: `Bạn muốn ${confirmAction} với tư cách là`,
      icon: 'info',
      input: 'radio',
      inputOptions,
      inputValue: 0,
      confirmButtonText: 'OK',
      inputValidator: value => {
        if (!value) {
          return 'Bạn chưa chọn vai trò!'
        }
        let userType = userOptions.enStudent
        let action = userOptions.enSignin
        if (confirmAction === userOptions.viSignup) {
          action = userOptions.enSignup
        }
        if (value === '1') {
          userType = userOptions.enTeacher
        }
        // eslint-disable-next-line no-undef
        window.location.href = `/${userType}/${action}`
        return null
      },
    })
  }

  const UserMenu = (
    <Menu>
      <Menu.Item style={{ cursor: 'default' }}>
        Xin chào, {currentUser && currentUser.displayName}
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item>
        {currentUser &&
          (currentUser.typeID === TEACHER ? (
            <Link to={`/teacher/info?id=${currentUser._id}`}>Trang cá nhân</Link>
          ) : (
            <Link to={`/student/info?id=${currentUser._id}`}>Trang cá nhân</Link>
          ))}
      </Menu.Item>
      <Menu.Item>
        {currentUser && <Link to={`/contract-list/${currentUser._id}`}>Hợp đồng</Link>}
      </Menu.Item>
      <Menu.Item>
        <Link to="/">Đổi mật khẩu</Link>
      </Menu.Item>
      <Menu.Item>
        <Link to="/">
          <div onClick={handleLogout}>Đăng xuất</div>
        </Link>
      </Menu.Item>
    </Menu>
  )

  return (
    <Header className="main-header">
      <div className="main-header__logo">
        <Link to="/">
          <img
            src="data:image/svg+xml;base64,"
            alt=""
          />
        </Link>
      </div>
      <div className="main-header__user">
        {currentUser ? (
          <Dropdown
            overlay={currentUser ? UserMenu : null}
            placement="bottomRight"
            getPopupContainer={trigger => trigger.parentNode}
          >
            <Avatar size="large" src={currentUser.avatar} />
          </Dropdown>
        ) : (
          <>
            <Button type="link" onClick={() => showConfirm(userOptions.viSignin)}>
              Đăng nhập
            </Button>
            <Button type="link" onClick={() => showConfirm(userOptions.viSignup)}>
              Đăng ký
            </Button>
          </>
        )}
      </div>
      <div className="main-header__search">
        <Search
          placeholder="Tìm kiếm"
          onSearch={value => console.log(value)}
          style={{ width: 200 }}
        />
      </div>
      <Menu
        getPopupContainer={node => node.parentNode}
        theme="light"
        mode="horizontal"
        defaultSelectedKeys={['2']}
        style={{ lineHeight: '64px' }}
      >
        <Menu.Item key="1">
          <Link to="/">Trang chủ</Link>
        </Menu.Item>
        <Menu.Item key="2">
          <Link to="/teacher">Gia sư</Link>
        </Menu.Item>
        <SubMenu
          key="sub1"
          title={
            <span>
              <span>Chuyên ngành</span>
            </span>
          }
        >
          <SubMenu key="sub1-1" title="CNTT">
            <Menu.Item key="3">Hệ điều hành</Menu.Item>
            <Menu.Item key="4">Cấu trúc dữ liệu và giải thuật</Menu.Item>
          </SubMenu>
          <Menu.Item key="5">Vật lý</Menu.Item>
          <SubMenu key="sub1-2" title="Toán">
            <Menu.Item key="6">Toán tổ hợp</Menu.Item>
            <Menu.Item key="7">Vi tích phân</Menu.Item>
          </SubMenu>
          <Menu.Item key="8">Hóa học</Menu.Item>
        </SubMenu>
      </Menu>
    </Header>
  )
}

MainHeader.propTypes = {}

export default MainHeader
